import react, { useState, useEffect } from 'react';
import { Table, TableHead, TableCell, TableRow, TableBody,makeStyles } from '@material-ui/core'
import { getUsers, deleteUser, editUser } from './get';
import Dialog from '@mui/material/Dialog';
import {Try} from '../components/try'
import React from 'react'
import { FormGroup, FormControl, InputLabel, Input, Button, Typography } from '@material-ui/core';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import {EditUser} from '../page/index';
import { Link } from 'react-router-dom';
import TextField from '@mui/material/TextField';
// import { Modal} from 'react-bootstrap';
import { Modal} from 'react-bootstrap';
const useStyles = makeStyles({
    table: {
        width: '90%',
        margin: '50px 0 0 50px'
    },
    thead: {
        '& > *': {
            fontSize: 20,
            background: '#000000',
            color: '#FFFFFF'
        }
    },
    row: {
        '& > *': {
            fontSize: 18
        }
    }
})


const AllUsers = (user) => {
    const [users, setUsers] = useState([]);
    const classes = useStyles();

    useEffect(() => {
        getAllUsers();
    }, []);

    const deleteUserData = async (id) => {
        await deleteUser(id);
        getAllUsers();
    }

    const getAllUsers = async () => {
        let response = await getUsers();
        setUsers(response.data);
    }

    // const addUserDetails = async (id)=>{
    //     await editUser(id, user)
    //     setOpen(false);
    //     getAllUsers();
    // }

    const [open, setOpen] = React.useState(false);
    const handleClickOpen = () => {
        // console.log("hello");
        setOpen(true);
      };
      const handleClose = () => {
        setOpen(false);
      };


    return (
        <div>
        <Table className={classes.table}>
            <TableHead>
                <TableRow className={classes.thead}>
                    <TableCell>Id</TableCell>
                    <TableCell>Name</TableCell>
                    <TableCell>Username</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell>Phone</TableCell>
                    <TableCell>Action</TableCell>
                </TableRow>
            </TableHead>
            <TableBody>
                {users.map((user) => (
                    <TableRow className={classes.row} key={user._id}>
                        <TableCell>{user.id}</TableCell>
                        <TableCell>{user.name}</TableCell>
                        <TableCell>{user.username}</TableCell>
                        <TableCell>{user.email}</TableCell>
                        <TableCell>{user.phone}</TableCell>
                        <TableCell>

                            <Button color="primary"  onClick={ handleClickOpen}  variant="contained" data-toggle="modal" style={{marginRight:10}} >Edit</Button>

                            {/* <Button color="primary" onClick={() => addUserDetails()}   component={Link} to={`/editUser/${user.id}`}  variant="contained" data-toggle="modal" style={{marginRight:10}} >Edit</Button> */}
                            <Button color="secondary" variant="contained" onClick={() => deleteUserData(user.id)}>Delete</Button> 


                        </TableCell>
                    </TableRow>
                ))}
            </TableBody>
        </Table>

{/* <Modal show={open} onHide={handleClose}>
<Modal.Body>
    <EditUser initialValue={user.id}/>
</Modal.Body>
<Modal.Footer>
        <Button  color="secondary" variant="contained"  onClick={handleClose}>
            Close Button
        </Button>
</Modal.Footer>
</Modal> */}

<Dialog open={open} onClose={handleClose}>
        <DialogTitle>Edit Profile</DialogTitle>
        <DialogContent>
        <TextField
            autoFocus
            margin="dense"
            id="id"
            label="Id"
            type="number"
            fullWidth
            variant="standard"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
           <TextField
            autoFocus
            margin="dense"
            id="username"
            label="Userame"
            type="text"
            fullWidth
            variant="standard"
            value={name}
            onChange={(e) => setUserName(e.target.value)}
          />
           <TextField
            autoFocus
            margin="dense"
            id="place"
            label="Place"
            type="text"
            fullWidth
            variant="standard"
            value={username}
            onChange={(e) => setPlace(e.target.value)}
          />
           <TextField
            autoFocus
            margin="dense"
            id="email"
            label="email"
            type="number"
            fullWidth
            variant="standard"
            value={email}
            onChange={(e) => setContact(e.target.value)}
          />
            <TextField
            autoFocus
            margin="dense"
            id="phone"
            label="phone"
            type="number"
            fullWidth
            variant="standard"
            value={phone}
            onChange={(e) => setContact(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={editUSer}>Edit</Button>
        </DialogActions>
      </Dialog>

</div>
    )
}

export default AllUsers;
