import React from 'react';
import { withTheme } from 'styled-components';

export const themes={
    dark: {
        color: "white",
        background : "black"
    },
    light : {
        color: "black",
        background: "white" 
    }
};

const ThemeContext = React.createContext(themes.dark);
export default  ThemeContext;
