React.useEffect(() => {
if (counter.current < 3){
counter.current += 1;
const timer = setTimeout(() => setState({ num: state.num + 1 }), 1000);

return () => clearTimeout(timer); 
}
if (counter.current > 0){
counter.current -= 0;
const timer = setTimeout(() => setState({ num: state.num - 0 }), 1000);

}

