import react, { useState, useEffect } from 'react';
import { Table, TableHead, TableCell, Paper, TableRow, TableBody, Button, makeStyles } from '@material-ui/core'
import { getUsers, deleteUser, editUser } from './get';
import Dialog from '@mui/material/Dialog';
import {Try} from '../components/try'
import React from 'react'

import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import {EditUser} from '../page/index';
import { Link } from 'react-router-dom';
// import { Modal} from 'react-bootstrap';
import { Modal} from 'react-bootstrap';
const useStyles = makeStyles({
    table: {
        width: '90%',
        margin: '50px 0 0 50px'
    },
    thead: {
        '& > *': {
            fontSize: 20,
            background: '#000000',
            color: '#FFFFFF'
        }
    },
    row: {
        '& > *': {
            fontSize: 18
        }
    }
})


const AllUsers = (user) => {
    const [users, setUsers] = useState([]);
    const classes = useStyles();

    useEffect(() => {
        getAllUsers();
    }, []);

    const deleteUserData = async (id) => {
        await deleteUser(id);
        getAllUsers();
    }

    const getAllUsers = async () => {
        let response = await getUsers();
        setUsers(response.data);
    }

    // const addUserDetails = async (id)=>{
    //     await editUser(id, user)
    //     setOpen(false);
    //     getAllUsers();
    // }

    const [open, setOpen] = React.useState(false);
    const handleClickOpen = () => {
        // console.log("hello");
        setOpen(true);
      };
      const handleClose = () => {
        setOpen(false);
      };


    return (
        <div>
        <Table className={classes.table}>
            <TableHead>
                <TableRow className={classes.thead}>
                    <TableCell>Id</TableCell>
                    <TableCell>Name</TableCell>
                    <TableCell>Username</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell>Phone</TableCell>
                    <TableCell>Action</TableCell>
                </TableRow>
            </TableHead>
            <TableBody>
                {users.map((user) => (
                    <TableRow className={classes.row} key={user._id}>
                        <TableCell>{user.id}</TableCell>
                        <TableCell>{user.name}</TableCell>
                        <TableCell>{user.username}</TableCell>
                        <TableCell>{user.email}</TableCell>
                        <TableCell>{user.phone}</TableCell>
                        <TableCell>

                            <Button color="primary"  onClick={ handleClickOpen}  variant="contained" data-toggle="modal" style={{marginRight:10}} >Edit</Button>

                            {/* <Button color="primary" onClick={() => addUserDetails()}   component={Link} to={`/editUser/${user.id}`}  variant="contained" data-toggle="modal" style={{marginRight:10}} >Edit</Button> */}
                            <Button color="secondary" variant="contained" onClick={() => deleteUserData(user.id)}>Delete</Button> 


                        </TableCell>
                    </TableRow>
                ))}
            </TableBody>
        </Table>

{/* <Modal show={open} onHide={handleClose}>
<Modal.Body>
    <EditUser initialValue={user.id}/>
</Modal.Body>
<Modal.Footer>
        <Button  color="secondary" variant="contained"  onClick={handleClose}>
            Close Button
        </Button>
</Modal.Footer>
</Modal> */}

<Dialog open={open} onClose={handleClose}>
        <DialogTitle>Edit Profile</DialogTitle>
        <DialogContent>
<FormGroup className={classes.container}>
            <Typography variant="h4">Edit Information</Typography>
            <FormControl>
                <InputLabel htmlFor="my-input">Name</InputLabel>
                <Input onChange={(e) => onValueChange(e)} name='name' value={name} id="my-input" aria-describedby="my-helper-text" />
            </FormControl>
            <FormControl>
                <InputLabel htmlFor="my-input">Username</InputLabel>
                <Input onChange={(e) => onValueChange(e)} name='username' value={username} id="my-input" aria-describedby="my-helper-text" />
            </FormControl>
            <FormControl>
                <InputLabel htmlFor="my-input">Email</InputLabel>
                <Input onChange={(e) => onValueChange(e)} name='email' value={email} id="my-input" aria-describedby="my-helper-text" />
            </FormControl>
            <FormControl>
                <InputLabel htmlFor="my-input">Phone</InputLabel>
                <Input onChange={(e) => onValueChange(e)} name='phone' value={phone} id="my-input" aria-describedby="my-helper-text" />
            </FormControl>
            <FormControl>

                <Button variant="contained" color="primary" onClick={() => editUserDetails(user.id)}>Edit User</Button>
            </FormControl>
        </FormGroup>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={editUSer}>Edit</Button>
        </DialogActions>
      </Dialog>

</div>
    )
}

export default AllUsers;
