          return (
            <Button  
            // onClick={() => deleteUser(data)}
              variant="contained"
              color="secondary"
              startIcon={<DeleteIcon />} >
              Delete
            </Button>
          );
        }
      }
    ];

const Get =()=>{

    const [tableData, setTableData]= useState([])
    useEffect(() => {
      fetch('http://localhost:3001/users')
        .then((data) => data.json())
        .then((data) => setTableData(data))
    }, [])

